import { createSlice } from "@reduxjs/toolkit"

const initialState = {
  Recipes: [
    {
      id: "1",
      name: "Burger",
      description: "Boys Undergoing Rigorous Genius Expos√©s Regularily.",
      recipeImg:
        "data:image/jpeg;base64,",
      ingredients: "sliced onion,tomato,butter",
      favorite: false,
    },
    {
      id: "2",
      name: "Manchurian",
      description: "Healthy Manchurian Calories per serving are 205 calories.",
      recipeImg:
        "data:image/jpeg;base64,",
      ingredients: "onion,cauliflower,soy sauce",
      favorite: false,
    }
  ],
  showRecipeForm: false,
  showEditForm:false,
  showIngredientForm:false,
  selectedRecipe:"",
  EditFormInitialValues:{
        id: "",
        name: "",
        description: "",
        recipeImg:"",
        ingredients: "",
        favorite: "",
  },
  errorMsg:null,
}

export const RecipeSlice = createSlice({
  name: "RecipeSlice",
  initialState: initialState,
  reducers: {
    addRecipe(state, action) {
      let newArr = [action.payload, ...state.Recipes]
      state.Recipes = newArr
    },
    deleteRecipe(state,action){
        let newArr = [...action.payload]
        state.Recipes = newArr
    },
    ToggleForm(state, action) {
      state.showRecipeForm = !state.showRecipeForm
    },
    ToggleEditForm(state, action) {
      state.showEditForm = !state.showEditForm
    },
    ToggleIngredientForm(state, action) {
      state.showIngredientForm = !state.showIngredientForm
    },
    setEditFormInitialValues(state,action){
        // console.log(action.payload[0])
        state.EditFormInitialValues = action.payload[0]
    },
    editRecipe(state,action){
        state.Recipes = action.payload
    },
    setSelectedRecipe(state,action){
        state.selectedRecipe = action.payload
    },
    setErrorMsg(state,action){
        state.errorMsg = action.payload
    },
  }
})

const RecipeActions = RecipeSlice.actions
export default RecipeActions
